<?php

namespace AppBundle\Repository;

use AppBundle\Entity\Fundraiser;
use Doctrine\Bundle\DoctrineBundle\Repository\ServiceEntityRepository;
use Doctrine\Persistence\ManagerRegistry;

/**
 * FundraiserRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class FundraiserRepository extends ServiceEntityRepository
{
    public function __construct(ManagerRegistry $registry)
    {
        parent::__construct($registry, Fundraiser::class);
    }

    public function getFundraisersByAverageRating(string $orderBy): array
    {
        $em = $this->getEntityManager();

        $queryString = '
            SELECT f, AVG(r.rating) as HIDDEN avg_rating
            FROM AppBundle:Fundraiser f
            LEFT JOIN AppBundle:Review r WITH f.id = r.fundraiser
            GROUP BY f.id ORDER BY';

        $queryString .= ($orderBy === "avg_rating") ? " avg_rating DESC" : " f.name ASC";

        $query = $em->createQuery($queryString);
        $result = $query->getResult();

        return $result;
    }
}
